INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.004", "Ricochet", 1.0, "Order", NULL, "Ranged", "0", NULL, NULL, NULL, "Whenever an allied card would deal <b>Piercing</b> damage to a recruited card this turn, instead you may deal that damage to a different fortification that shares a lane with the original fortification the card attacked. ");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.005", "Point-Blank", 2.0, "Order", NULL, "Ranged", "0", NULL, NULL, NULL, "The first time an allied card arrives at a fortification this turn, you may remove any number of Ammo counters from it. If you do, it gains {p} equal to the number of Ammo counters removed.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.006", "Flight Arrows", 1.0, "Order", NULL, "Ranged", "1", NULL, NULL, NULL, "<b>Ammo</b>
Target ranged card gains an Ammo counter and the <b>Long-distance</b> keyword until the end of this turn.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.007", "Piercing Bolts", 1.0, "Order", NULL, "Ranged", "1", NULL, NULL, NULL, "<b>Ammo</b>
Target ranged card gains an Ammo counter and the <b>Piercing</b> keyword until the end of this turn.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.008", "Shrapnel Shot", 1.0, "Order", NULL, "Ranged", "1", NULL, NULL, NULL, "The next time target ranged card would deal damage to a fortification this turn, that damage may instead be distributed to any number of recruited cards that share a field with the fortification.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.009", "Concussion Shot", 1.0, "Order", NULL, "Ranged", "1", NULL, NULL, NULL, "The next time target ranged card would deal damage to a recruited card this turn, instead remove that card from its recruitment rail.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.010", "Pick Off", 1.0, "Reinforcement", NULL, "Ranged", NULL, NULL, NULL, NULL, "Deal 1 damage to any card an opponent has recruited or deployed.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.011", "A Mile Away", 1.0, "Reinforcement", NULL, "Ranged", NULL, NULL, NULL, NULL, "Prevent all damage dealt to recruited allied cards that share a field with this reinforcement until the end of this turn.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.012", "Exposed Sightline", 1.0, "Reinforcement", NULL, "Ranged", NULL, NULL, NULL, NULL, "Reveal target card an opponent controls that shares a field with this reinforcement.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.013", "Resupply Run", 1.0, "Reinforcement", NULL, "Ranged", NULL, NULL, NULL, NULL, "Target ranged card gains an Ammo counter.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.014", "Two in the Chamber", 1.0, "Response", NULL, "Ranged", "1", NULL, NULL, NULL, "<b>Trigger:</b> Allied <b>Ammo</b> card is played.
<b>Response:</b> You may play an additional <b>Ammo</b> card without paying its {c}.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.015", "Watchful Sentry", 1.0, "Response", NULL, "Ranged", "1", NULL, NULL, NULL, "<b>Trigger:</b> Allied fortification is attacked by target card controlled by an opponent.
<b>Response:</b> Remove an ammo counter from a recruited allied ranged unit. If you do, deal damage equal to the unit's {p} to the target card.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.016", "Bound Back", 1.0, "Response", NULL, "Ranged", "0", NULL, NULL, NULL, "<b>Trigger:</b> Target <b>Long-distance</b> card arrives at a fortification and activates a reinforcement card.
<b>Response:</b> Redeploy target <b>Long-distance</b> card to the same lane and skip the arrival step. 
<i>(The activated reinforcement card's effect still resolves)</i>");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.017", "Bunker Buster Bolts", 2.0, "Order", NULL, "Ranged", "2", NULL, NULL, NULL, "<b>Ammo</b>
Target ranged card gains an Ammo counter and +1 {p} while attacking fortifications this turn.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.018", "Arc Arrows", 2.0, "Order", NULL, "Ranged", "2", NULL, NULL, NULL, "<b>Ammo</b>
