INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.019", "Firing Line", 2.0, "Order", NULL, "Ranged", "1", NULL, NULL, NULL, "For each Ammo counter on cards you have recruited, deal 1 damage to a target fortification controlled by an opponent. You cannot declare deployments this turn.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.020", "Practice Makes Perfect", 1.0, "Order", NULL, "Ranged", "0", NULL, NULL, NULL, "Until the end of this turn, whenever an allied card would gain <b>Long-distance</b> or <b>Piercing</b> while already having the given keyword, place an Ammo counter on that card.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.021", "Return Fire", 2.0, "Reinforcement", NULL, "Ranged", NULL, NULL, NULL, NULL, "The first time an attached fortification takes damage this turn, you may destroy an allied ranged unit on a recruitment rail that shares a field with the fortification. If you do, deal damage equal to your destroyed unit's {p} to all cards an opponent has recruited to that field.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.022", "Snare", 2.0, "Reinforcement", NULL, "Ranged", NULL, NULL, NULL, NULL, "Return the first card that arrives at an attached fortification to its recruitment rail and <b>equip</b> this card to it. While this is equipped, parent card cannot be deployed.
<b>Equip</b> <i>(Attach this card to another card. It remains attached until the parent card is destroyed.)</i>
<b>Once per Turn Order (1 {d}):</b> Destroy this card.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.023", "Scouting Report", 2.0, "Response", NULL, "Ranged", "0", NULL, NULL, NULL, "<b>Trigger:</b> Target card is deployed.
<b>Response:</b> Target card skips the arrival step.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.024", "Push Through", 2.0, "Response", NULL, "Ranged", "0", NULL, NULL, NULL, "<b>Trigger:</b> Target <b>Piercing</b> card arrives at a fortification and activates a reinforcement card.
<b>Response:</b> Remove an Ammo counter from target card. If you do, negate the activated reinforcement card and destroy it.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.025", "One Bird With Two Stones", 2.0, "Response", NULL, "Ranged", "1", NULL, NULL, NULL, "<b>Trigger:</b> An <b>Ammo</b> card is played.
<b>Response:</b> You may search your deck for an <b>Ammo</b> card with the same name, and play it without paying its {c}.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.026", "Rain Hell", 3.0, "Order", NULL, "Ranged", "1", NULL, NULL, NULL, "Any number of <b>Long-distance</b> cards may be deployed to the same lane until the end of this turn. Any <b>Long-distance</b> card deployed this turn must skip the arrival step.");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.027", "Rapid Fire", 3.0, "Order", NULL, "Ranged", "1", NULL, NULL, NULL, "Whenever a ranged card arrives at a fortification this turn, you may discard a card. If you do, the arriving card attacks twice in the attack step until the end of this turn. (<i>You may choose different targets for the attacks</i>).");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.028", "Surplus Munitions", 3.0, "Response", NULL, "Ranged", "1", NULL, NULL, NULL, "<b>Trigger:</b> An <b>Ammo</b> card is played.
<b>Response:</b> Apply the effect of that <b>Ammo</b> card to all allied ranged cards.  ");
INSERT OR REPLACE INTO Cards(id, name, rank, type, class, attribute, cost, power, life, hand, text) VALUES ("ALP.RNG.029", "Ramparts", NULL, "Fortification", NULL, "Ranged", NULL, NULL, 7.0, NULL, "The first time a card arrives at this fortification each turn, you may destory a ranged card on an allied recruitment rail. If you do, deal 1 damage to the arriving card.");